const glob = require('glob-promise')
/**
 * If you want to look in other folder for routes then you need to pass an option object with routes key.
 * example: {routes:'/routes/*.js'}
**/
exports.register = async  (server, options) =>{
    const routesPath = options.routes || '**/**/routes/*.js';
    let routesFiles = await glob.promise(routesPath);
    routesFiles.map(item =>server.route(require(`${process.cwd()}/${item}`)))
};

exports.name="routing"
